targets.query:
from(bucket:"opnsense")
  |> range(start: -24h)
  |> filter(fn: (r) =>
    r._measurement == "cpu" and
    r._field == "usage_idle" and
    r.cpu == "cpu-total"
  )
  |> map(fn: (r) => ({
        r with
        _value: r._value * -1.0 + 100.0
      })
    )
  |> keep(columns: ["_value"])
  |> last()

from(bucket:"opnsense")
  |> range(start: -24h)
  |> filter(fn: (r) =>
    r.host =~ /^${Host:regex}$/ and
    r._measurement == "cpu" and
    r._field == "usage_idle" and
    r.cpu =~ /cpu[0-3]$/ 
  )
  |> map(fn: (r) => ({
        r with
        _value: r._value * -1.0 + 100.0
      })
    )
  |> aggregateWindow(every: 10s, fn: mean)
  |> fill(usePrevious: true)

from(bucket: "opnsense")
  |> range(start: -24h)
  |> filter(fn: (r) => 
    r.host =~ /^${Host:regex}$/ and
    r.interface =~ /^${WAN:regex}$/ and
    r._measurement == "net" and
    r._field =~ /bytes_recv|bytes_sent/
  )
  |> aggregateWindow(every: 10s, fn: first)
  |> derivative(unit:1s)
  |> map(fn: (r) => ({
      r with
      _value: r._value * 8.0
    })
  )
  |> map(fn: (r) => ({_value:r._value, _time:r._time, _field:r._field}))

from(bucket: "opnsense")
  |> range(start: -24h)
  |> filter(fn: (r) => 
    r.host =~ /^${Host:regex}$/ and
    r.interface =~ /^${LAN:regex}$/ and
    r._measurement == "net" and
    r._field =~ /bytes_recv|bytes_sent/
  )
  |> aggregateWindow(every: 10s, fn: first)
  |> derivative(unit:1s)
  |> map(fn: (r) => ({
      r with
      _value: r._value * 8.0
    })
  )
  |> map(fn: (r) => ({_value:r._value, _time:r._time, _field:r._field}))
